using System.Collections;using System.Collections.Generic;using UnityEngine;    public class TutorialColliders : MonoBehaviour    {        public GameObject obj = null;        public GameObject WelcomeText;        public GameObject JumpText;        public GameObject DeathText;        public GameObject LadderText;        public GameObject CrouchText;        public GameObject EnemyText;        public GameObject GemText;        public GameObject TruckText;                                void OnTriggerEnter2D(Collider2D collider){            if(collider.CompareTag("Welcome")){                obj = collider.gameObject;                WelcomeText.SetActive(true);            }            else if (collider.CompareTag("Jump"))            {                obj = collider.gameObject;                JumpText.SetActive(true);            }            else if (collider.CompareTag("Death"))            {                obj = collider.gameObject;                DeathText.SetActive(true);            }            else if (collider.CompareTag("Ladder"))            {                obj = collider.gameObject;                LadderText.SetActive(true);            }            else if (collider.CompareTag("Crouch"))            {                obj = collider.gameObject;                CrouchText.SetActive(true);            }            else if (collider.CompareTag("Enemy"))            {                obj = collider.gameObject;                EnemyText.SetActive(true);            }            else if (collider.CompareTag("GemText"))            {                obj = collider.gameObject;                GemText.SetActive(true);            }            else if (collider.CompareTag("Truck"))            {                obj = collider.gameObject;                TruckText.SetActive(true);            }                    }                void OnTriggerExit2D(Collider2D collider){            if(collider.CompareTag("Welcome") && obj == collider.gameObject){                obj = null;                WelcomeText.SetActive(false);            }            else if(collider.CompareTag("Jump") && obj == collider.gameObject){                obj = null;                JumpText.SetActive(false);            }            else if(collider.CompareTag("Death") && obj == collider.gameObject){                obj = null;                DeathText.SetActive(false);            }            else if(collider.CompareTag("Ladder") && obj == collider.gameObject){                obj = null;                LadderText.SetActive(false);            }            else if(collider.CompareTag("Crouch") && obj == collider.gameObject){                obj = null;                CrouchText.SetActive(false);            }            else if(collider.CompareTag("Enemy") && obj == collider.gameObject){                obj = null;                EnemyText.SetActive(false);            }            else if(collider.CompareTag("GemText") && obj == collider.gameObject){                obj = null;                GemText.SetActive(false);            }            else if(collider.CompareTag("Truck") && obj == collider.gameObject){                obj = null;                TruckText.SetActive(false);            }        }            }